for (const item of items) {
  const html = item.json.data;
  const targetLang = item.json.language || 'en'; // Default to English

  // Extract the transcript data
  const transcriptMatch = html.match(/"captionTracks":\[(.*?)\]/);
  if (!transcriptMatch) {
    item.json = { error: 'No transcript data found' };
    continue;
  }

  // Parse the caption tracks
  const captionTracks = JSON.parse(`[${transcriptMatch[1]}]`);

  // Function to find a transcript by language
  const findTranscriptByLanguage = (lang) => {
    return captionTracks.find(track => 
      track.languageCode === lang || 
      track.vssId?.includes(`.${lang}`)
    );
  };

  // Attempt to find the target language transcript
  let transcript = findTranscriptByLanguage(targetLang);

  // If not found, try auto-generated version
  if (!transcript) {
    transcript = captionTracks.find(track => 
      track.kind === "asr" && 
      (track.languageCode === targetLang || track.vssId?.includes(`.${targetLang}`))
    );
  }

  // If still not found, iterate through all available languages
  if (!transcript) {
    for (const track of captionTracks) {
      transcript = findTranscriptByLanguage(track.languageCode);
      if (transcript) break;
    }
  }

  // Handle the result
  if (transcript) {
    item.json.transcriptUrl = transcript.baseUrl;
    item.json.languageUsed = transcript.languageCode;
  } else {
    item.json = { 
      error: `No transcript found for ${targetLang} or other available languages.`,
      availableLanguages: captionTracks.map(track => ({
        code: track.languageCode,
        kind: track.kind,
        name: track.name?.simpleText
      }))
    };
  }
}

return items;

