function getVideoId(url) {
  // Handle youtu.be format
  if (url.includes('youtu.be/')) {
    const id = url.split('youtu.be/')[1].split('?')[0];
    return id;
  }

  // Handle youtube.com format
  if (url.includes('youtube.com/watch')) {
    const matches = url.match(/[?&]v=([^&#]+)/);
    return matches ? matches[1] : null;
  }

  return null;
}

async function processSingle(item, index) {
  const url = item?.json?.url || item?.url;
  const lang = item?.json?.language || 'ko'; // Default to Korean if not specified

  if (!url) {
    return {
      json: {
        error: `No URL found in item ${index}`,
        inputReceived: JSON.stringify(item)
      }
    };
  }

  try {
    const videoId = getVideoId(url);
    if (!videoId) {
      return {
        json: {
          error: 'Could not extract video ID from URL',
          url: url
        }
      };
    }

    return {
      json: {
        videoId,
        language: lang,
        pageUrl: `https://www.youtube.com/watch?v=${videoId}`,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
          'Accept-Language': `${lang},en-US;q=0.9`  // Prioritize the requested language
        }
      }
    };
  } catch (error) {
    return {
      json: {
        error: error.message,
        errorDetail: error.toString(),
        url,
        inputItem: JSON.stringify(item)
      }
    };
  }
}

// Process all items
const results = await Promise.all(items.map(processSingle));
return results;

