// Get all inputs from the previous node
const inputs = $input.all();

// Function to extract text between tags
function extractTextBetweenTags(text, tagName) {
  const regex = new RegExp(`<${tagName}[^>]*>(.*?)</${tagName}>`, 'gi');
  const matches = [];
  let match;
  while ((match = regex.exec(text)) !== null) {
    matches.push(match[1].trim());
  }
  return matches;
}

// Initialize an array to store results
const results = [];

// Process each input item
for (const item of inputs) {
  // Get the HTML content from the current item
  const html = item.json.html || item.json.content || item.html || item.content;

  if (html) {
    // Extract content from title tags
    const titleContents = extractTextBetweenTags(html, 'title');

    // Add the result to the results array
    results.push({ json: { titleContents } });
  }
}

// Return the results
return results;
