// ```과 ``` 사이의 텍스트를 추출하는 함수
function extractTextBetweenBackticks(text) {
  const regex = /```(.*?)```/gs; // ```와 ``` 사이의 텍스트를 캡처하는 정규식
  const matches = [];
  let match;
  while ((match = regex.exec(text)) !== null) {
    matches.push(match[1].trim());
  }
  return matches;
}

// 결과를 저장할 배열 초기화
