  const content = item.json.input || item.json.text || item.json.content || item.text || item.content;

  if (content) {
    // ```과 ``` 사이의 내용을 추출합니다
    const extractedContents = extractTextBetweenBackticks(content);

    // 결과를 results 배열에 추가합니다
